handlers.handler:
switch (event.keyCode) {
	case KeyEvent.DOM_VK_UP:
		this.firePathBar.selector = this.firePathBar._history.element[this.firePathBar._pointer].locator;
		this.firePathBar.locator = this.firePathBar._history.element[this.firePathBar._pointer].key;
		this.checkSyntax();
		this.firePathBar.evaluate();
		this.firePathBar._pointer++;
		var itemCounter = this.firePathBar.getElement("firepath-menulist").itemCount;
		if(this.firePathBar._pointer == itemCounter){
			this.firePathBar._pointer=0;
		}
		break;
	case KeyEvent.DOM_VK_DOWN:
		this.firePathBar.selector = this.firePathBar._history.element[this.firePathBar._pointer].locator;
		this.firePathBar.locator = this.firePathBar._history.element[this.firePathBar._pointer].key;
		this.checkSyntax();
		this.firePathBar.evaluate();
		this.firePathBar._pointer--;
		var itemCounter = this.firePathBar.getElement("firepath-menulist").itemCount;
		if(this.firePathBar._pointer < 0){
			this.firePathBar._pointer = itemCounter-1;
		}
		break;
	case KeyEvent.DOM_VK_RETURN:
		this.firePathBar.evaluate();
		var current = {"key":this.firePathBar.locator, "locator":this.firePathBar.selector};						
		this.firePathBar._history.element.push(current);
		break;
	//case KeyEvent.DOM_VK_TAB:
		//event.preventDefault();
		//event.stopPropagation();

}

