		var current = {"key":this.firePathBar.locator, "locator":this.firePathBar.selector};
		var lastLocatorPair = this.firePathBar._history.element[itemCounter-1];
		var lastKey = lastLocatorPair.key;
		var lastLocator = lastLocatorPair.locator;

		if(!(lastKey==current.key && lastLocator==current.locator) && current.locator.length>0){
			this.firePathBar.addLocator(current.key, itemCounter, current.locator);
			this.firePathBar.addSelector(current.locator, itemCounter, current.key);
			this.firePathBar._history.element.push(current);
		}
		break;
	//case KeyEvent.DOM_VK_PAGE_DOWN:
