if (elementInfo[4] == 1) {

	if (this.elementType) {// new container
		if (locator.length > 0) {
			var con = confirm("Container Name: " + locator + "\r\nSelector: "
					+ selector);
			if (con) {
				this.insertLocator2Sqlite(locator, selector, elementInfo[1],
						elementInfo[2], elementInfo[3], this.elementType);
			}
		} else {
			alert("Key is empty!");
			return false;
		}

	} else {// new element

		if (selector.length > 0) {// selector is valid
			for (control in myControls) {
				if (myControls[control] == "a") {
					if (selector.indexOf(myControls[control]) == 0) {
						currentIndex = 0;
					}
				} else {
					currentIndex = selector.lastIndexOf(myControls[control]);
				}

				if (currentIndex > maxIndex) {
					maxIndex = currentIndex;
					currentControl = myControls[control];
				}
			}

			switch (currentControl) {
				case "input" :
					prefix = "ipt";
					break;
				case "button" :
					prefix = "btn";
					break;
				case "link" :
				case "a" :
				case ">a" :
				case " a" :
					prefix = "lnk";
					break;
				case "select" :
					prefix = "slt";
					break;
				case "checkbox" :
					prefix = "ckb";
					break;
				case "image" :
				case "img" :
					prefix = "img";
					break;
				default :
					prefix = "obj";
			}

			if (locator.length == 0) {
				locator = "WebElement" + this._counter;
				this._counter++;
			}

			if (this.parentToolbar.collapsed)
				this.parentSelector = "";

			var parentContext = this.parentSelector;
			if (parentContext.length < 1 || parentContext == "/") {
				parentContext = "document";
			}

			//var data = "String " + prefix + locator + " = \"" + selector + "\";\r\n";
			var data = "Key: " + prefix + locator + "\r\nLocator: " + selector;
			var con = confirm("Parent Context: " + parentContext + "\r\n"
					+ data);
			if (con) {
				this.insertLocator2Sqlite(prefix + locator, selector,
						elementInfo[1], elementInfo[2], elementInfo[3],
						this.elementType);

				// this.insertLocator2Sqlite(prefix + locator, selector,
